mapper.select:
select top ${pageSize}
wall.*,hd.hd_name as action_name
from [wd_eleven_comment_wall_tb] wall left join hd_config_tb hd
on wall.action_code=hd.code



			and wall.[code] < (
			select min(tblTmp.[code]) from
			(
			select
			top
			${forwardPage}
			wall.[code] from [wd_eleven_comment_wall_tb] wall left join hd_config_tb hd
on wall.action_code=hd.code where
			${strWhere}
			order by
			wall.[code] desc
			) as tblTmp
			) and ${strWhere}


			and ${strWhere}



order by wall.[code] desc
