mapper.update:
update action_common_image
set
temp_url = #{tempUrl},real_url = #{realUrl},img_width = #{imgWidth},img_height = #{imgHeight},img_feature = #{imgFeature},status = #{status}
where
code = #{code}

mapper.select:
select action_common_image.*,t.* from ( select action_common_image.code,ROW_NUMBER() OVER (ORDER BY action_common_image.code desc) as m from action_common_image left join action_common_form on action_common_image.form_code = action_common_form.code where (action_common_image.action_code=${actionCode}
and action_common_image.status = '${status}'
and action_common_image.status != '审核未通过'
) and (action_common_form.json_str like '%${account}%' and action_common_form.json_str like '%${area}%' and action_common_form.json_str like '%${netType}%') ) as t left join action_common_image on t.code = action_common_image.code where t.m between ((${pageIndex}*10) + 1) and ((${pageIndex} + 1)*10)

select action_common_image.*,t.* from ( select action_common_image.code,ROW_NUMBER() OVER (ORDER BY action_common_image.code desc) as m from action_common_image left join action_common_form on action_common_image.form_code = action_common_form.code where (action_common_image.action_code=${actionCode}
and action_common_image.status = '${status}'
and action_common_image.status != '审核未通过'
) and (action_common_form.json_str like '%${account}%' and action_common_form.json_str like '%${area}%' and action_common_form.json_str like '%${netType}%') ) as t left join action_common_image on t.code = action_common_image.code
