mapper.select:
    select count(code) from action_user_login_log 
    where 1=1  

		 and CONVERT(varchar(10), login_time, 21) =  #{loginTimeStr}   


		and account =  #{account} 


		and login_id =  #{loginId}

select TOP (#{pageSize}) A.* from ( SELECT TOP (#{pageSize}*#{pageIndex}) ROW_NUMBER() OVER (ORDER BY a.login_time desc) AS row_num,a.* FROM action_user_login_log a where 1=1
and CONVERT(varchar(10), login_time, 21) = #{loginTimeStr}
and account = #{account}
order by a.login_time desc ) A WHERE row_num > (#{pageSize}*(#{pageIndex}-1))
