mapper.select:
    select 

    from user_tb u 
		left join user_info_tb info on u.code = info.user_id
	    left join user_head_info_tb head on u.code = head.user_id
    left join user_inventory_tb inventory on u.code = inventory.user_id
    where parent_id = #{userId,jdbcType=INTEGER}

      and status = #{status,jdbcType=VARCHAR}

    order by u.create_time desc
    limit #{offset,jdbcType=INTEGER},#{rows,jdbcType=INTEGER}

mapper.update:
update user_tb
set password = #{password,jdbcType=CHAR} 
where code = #{code,jdbcType=INTEGER}
